package com.example.sgroser.activities;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;

import android.os.Bundle;

import com.example.sgroser.adapter.CategoryAdapter;
import com.example.sgroser.adapter.ProductAdapter;
import com.example.sgroser.databinding.ActivityMainBinding;
import com.example.sgroser.models.CategoryModel;
import com.example.sgroser.models.ProductModel;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    ActivityMainBinding binding;
    CategoryAdapter categoryAdapter;
    ArrayList<CategoryModel> arrCategory;

    ProductAdapter productAdapter;
    ArrayList<ProductModel> arrProduct;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding=ActivityMainBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        intiGetCategory();
        initGetProduct();


    }private void initGetProduct(){
        arrProduct=new ArrayList<>();
        arrProduct.add(new ProductModel("Cloth","","https://www.oxford.com.pk/cdn/shop/files/MAROON1_2f8bbc23-5423-430d-a0cc-79e991f743a7.jpg?v=1686925512",12,50,1));
        arrProduct.add(new ProductModel("Computer","https://www.trurocomputerservices.co.uk/wp-content/uploads/2021/03/gaming-pc-truro.jpg","",12,15000,2));
        arrProduct.add(new ProductModel("M10 AirPod","https://static-01.daraz.pk/p/5a42d4d77bb6f0a51ea394abdfa5fab5.jpg_750x750.jpg_.webp","",12,1000,3));
        arrProduct.add(new ProductModel("Keyboard","https://images.unsplash.com/photo-1612198188060-c7c2a3b66eae?q=80&w=1000&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8Z2FtaW5nJTIwa2V5Ym9hcmR8ZW58MHx8MHx8fDA%3D","",12,500,4));
        arrProduct.add(new ProductModel("Phone","","data:image/jpeg;base64,",12,35000,4));
        arrProduct.add(new ProductModel("Book","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQVVW92n6qsqOyUrW_DcswO-pbK1UxxiRZLpw&usqp=CAU","",12,800,5));
        arrProduct.add(new ProductModel("Suit","https://andreemilio.com/wp-content/uploads/2021/03/Men-Navy-Blue-Suit-With-Check-Waistcoat-2.jpg","",12,5000,6));

        productAdapter =new ProductAdapter(this,arrProduct);

        binding.productRecycler.setLayoutManager(new GridLayoutManager(this,2));
        binding.productRecycler.setAdapter(productAdapter);

    }private void intiGetCategory(){
        arrCategory=new ArrayList<>();
        arrCategory.add(new CategoryModel("Grocery","https://media.istockphoto.com/id/164981421/photo/large-group-of-food-shoot-on-white-backdrop.jpg?s=612x612&w=0&k=20&c=S3UjegrKBG-HyZdYQmOeBCk1Cfk7C7XZrUGb0n56Gy8=","We have Fresh Grocery and Fruits ","#FF000000",1));
        arrCategory.add(new CategoryModel("Mart","https://valuemart.co.in/images/produ-2.jpg","Big Mart","#FFFFFFFF",2));
        arrCategory.add(new CategoryModel("Mackup","https://cdn.britannica.com/35/222035-131-9FC95B31/makeup-cosmetics.jpg","Luxurious Markup Item","#00BCD4",3));
        arrCategory.add(new CategoryModel("Food","data:image/jpeg;base64,","Quality Food","#063C54",4));
        arrCategory.add(new CategoryModel("Lift Style","data:image/jpeg;base64,","Amazing Life Style Item","#A40121",5));
        arrCategory.add(new CategoryModel("Computer","data:image/jpeg;base64,","All Computer and Accessories","#E91E63",6));
        categoryAdapter=new CategoryAdapter(this,arrCategory);

        binding.categoriesRecycler.setLayoutManager(new GridLayoutManager(this,4));
        binding.categoriesRecycler.setAdapter(categoryAdapter);

    }
}